import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatefulWidget {
  const MyApp({Key? key}) : super(key: key);

  @override
  State<MyApp> createState() => _MainState();
}

class _MainState extends State<MyApp> {
  var title_list = ["Android", "Java", "Flutter"];
  var desc_list = ["Mobile", "Hybrid", "Programming"];
  var img_list = [
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/jpeg;base64,"
  ];
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: Scaffold(
        appBar: AppBar(
          title: Text("Myapp"),
        ),
        body: ListView.builder(
            itemCount: title_list.length,
            itemBuilder: ((context, index) {
              //we can use gesture detector or inkwell (ripple effect)
              //in gesture decotor we get more controllers
              return GestureDetector(
                  onTap: (() {
                    //on click event
                  }),
                  child: Card(
                    child: Row(
                      children: [
                        Container(
                          height: 100,
                          width: 100,
                          child: Image.network(img_list[index]),
                        ),
                        Container(
                          child: Text(title_list[index]),
                        )
                      ],
                    ),
                  ));
            })),
      ),
    );
  }
}
